import { useState, useEffect, useContext } from 'react';
import { useWeb3React } from '@web3-react/core';
import InjectedConnector from '../../../core/connectors/injectedConnector';
import mainnetConverter from '../../../utiles/mainnetConverter';
import WalletContext from '../../../contexts/WalletContext';

const Wallet = (props: any) => {
  const { account, activate, deactivate } = useWeb3React();
  const { address, active, userType, chainId, activateWallet } = useContext(WalletContext)


  return (
    <>
      <div className={`navigation__wallet${active ? "--connected" : ""}`}
        onClick={() => {
          console.log(active)
          if (!active) {
            activateWallet()
          }
        }}>
        <div className="navigation__wallet__wrapper">
          {active && (
            <p className="navigation__wallet__mainnet">{mainnetConverter(chainId)}</p>
          )}
          <p className={`navigation__wallet__status${active ? "--connected" : ""}`}>{active ? `${address?.slice(0, 6)}....${address?.slice(-4)}`: "Connect Wallet"}</p>
        </div>
      </div>
    </>
  );
}

export default Wallet;
