import { useContext, useState, useEffect, useCallback } from 'react';
import LanguageType from "../../../enums/LanguageType";
import LanguageContext from '../../../contexts/LanguageContext';
import ko from "../../../shared/images/korea@2x.png";
import cn from "../../../shared/images/china@2x.png";
import en from "../../../shared/images/america@2x.png";


export const LanguageConverter = () => {
  const { language, setLanguage } = useContext(LanguageContext);
  const array: LanguageType[] = [LanguageType.EN, LanguageType.KO, LanguageType.ZHHANS];
  const [state, setState] = useState({
    selectLanguage: language,
    isshowing: false,
    arrayHandler: array
  });

  useEffect(() => {
    const arrayLength = array.indexOf(language)
    console.log(arrayLength)

    if (arrayLength > -1 ) {
      setState({ ...state, arrayHandler: array.splice(arrayLength, 1) })
      console.log("array : " + array.splice(arrayLength, 1) )
    }
    setState({ ...state, selectLanguage: language })
    console.log("lang : " + state.selectLanguage)
  }, [language])

  const showingLangIcon = (lang: LanguageType) => {
    console.log("true lang : " + lang)
    switch (lang) {
      case LanguageType.KO:
        return (
          <>
            <img src={ko} /><p>KOR</p>
          </>
        )
      case LanguageType.ZHHANS:
        return (
          <>
            <img src={cn} /><p>CHA</p>
          </>
        )
      case LanguageType.EN:
      default:
        return (
          <>
            <img src={en} onClick={() => {
            }} /><p>ENG</p>
          </>
        )
    }
  }
  let ChangeLang = () => {

    // console.log(state.arrayHandler.indexOf(LanguageType.EN) && "EN ok")
    // console.log(state.arrayHandler.indexOf(LanguageType.KO) && "ko ok")
    // console.log(state.arrayHandler.indexOf(LanguageType.ZHHANS) && "ZHHANS ok")
    return (
      <div>
        <img 
            className="forum__footer-image" 
            src={en}
            // style={{ display: state.arrayHandler.includes(LanguageType.EN) ? "block" : "none" }}
            onClick={() => {
              // console.log(state.arrayHandler.includes(LanguageType.EN) && "EN ok")
              // console.log(state.arrayHandler.includes(LanguageType.KO) && "ko ok")
              // console.log(state.arrayHandler.includes(LanguageType.ZHHANS) && "ZHHANS ok")
              setLanguage(LanguageType.EN)
            }}/>
          <img className="forum__footer-image" src={ko}
            // style={{ display: state.arrayHandler.includes(LanguageType.KO) ? "block" : "none" }} 
            onClick={() => {
              // console.log(state.arrayHandler.includes(LanguageType.EN) && "EN ok")
              // console.log(state.arrayHandler.includes(LanguageType.KO) && "ko ok")
              // console.log(state.arrayHandler.includes(LanguageType.ZHHANS) && "ZHHANS ok")
              setLanguage(LanguageType.KO)
          }}/>
          <img className="forum__footer-image" src={cn}
            // style={{ display: state.arrayHandler.includes(LanguageType.ZHHANS) ? "block" : "none" }} 
            onClick={() => {
              // console.log(state.arrayHandler.includes(LanguageType.EN) && "EN ok")
              // console.log(state.arrayHandler.includes(LanguageType.KO) && "ko ok")
              // console.log(state.arrayHandler.includes(LanguageType.ZHHANS) && "ZHHANS ok")
              setLanguage(LanguageType.ZHHANS)
          }}/>
      </div>
    )
  }
  return (
   <div>
      <div className="lang__now-language">
        {ChangeLang()}
        <br/>
        {showingLangIcon(state.selectLanguage)}
      </div>
   </div>
 )

}


export default LanguageConverter;
