import { useState, useEffect } from 'react';
import { useWeb3React } from '@web3-react/core';
import InjectedConnector from '../../../core/connectors/injectedConnector';

import { ethers } from 'ethers';
import { useHistory } from 'react-router-dom';
import { useEagerConnect, useInactiveListener } from '../../../hooks/connectHoots';

type Account = string | null | undefined;

let throttle_flag = false;
const throttle = (func: () => void, delay: number) => {
  if (!throttle_flag) {
    func();
    throttle_flag = true;
    setTimeout(() => {
      throttle_flag = false;
    }, delay);
  }
};

const Wallet = (props: any) => {
  const { account, activate, deactivate, error, active, connector, library, chainId } = useWeb3React();
  const [connected, setConnected] = useState<boolean>(false);
  const [userAccount, setUserAccount] = useState<Account>(undefined);

  useEffect(() => {
    setConnected(!!account)
  }, [account])

  useEffect(() => {
    console.log("error : " + chainId)
  }, [])

  useEffect(() => {
    if (account) {
      throttle(() => setUserAccount(account), 500);
    }
  }, [account]);

  return (
    <>
    <div className={`navigation__wallet${connected && "--connected"}`}
      onClick={() => {
        if (!active) {
          activate(InjectedConnector)
        }
      }}>
      <p className="navigation__wallet__status">{connected ? userAccount : "Connect Wallet"}</p>
    </div>
    </>
  );
}

export default Wallet;
