import React from 'react';
import Navigation from '../component/Navigation';
import MainBackground from '../../../shared/images/main-background.png';
import DownArrow from '../../../shared/images/down-arrow.png';
import Service00 from '../../../shared/images/service00.png';
import Service01 from '../../../shared/images/service01.png';
import Service02 from '../../../shared/images/service02.png';
import Forum from '../forum/Forum';

import '../css/style.scss';

const Main: React.FC = () => {
  const MainRef = React.createRef<HTMLDivElement>();
  const ForumRef = React.createRef<HTMLDivElement>();
  const WorksRef = React.createRef<HTMLDivElement>();

  const Scroll = (ref: string) => {
    // ref.current.scrollIntoView({ behavior: 'smooth' })
    var element = document.getElementById(ref);
    const offset = 0;
    const bodyRect = document.body.getBoundingClientRect().top;
    const elementRect = element!.getBoundingClientRect().top;
    const elementPosition = elementRect - bodyRect;
    const offsetPosition = elementPosition - offset;

    window.scrollTo({
      top: offsetPosition,
      behavior: 'smooth'
    });
  }
  return (
    <div className={`elysia--pc`}>
      <section className="main" id="main" ref={MainRef} style={{ backgroundImage: `url(${MainBackground})` }}>
        <Navigation />
        <div className="main__content-container">
          <h1 className="main__content--bold">
            <span className="main__content--blue">ELYFI</span> : <br />Real asset liquidity protocol
          </h1>
          <p className="main__content">
            ELYFI is a new decentralized platfrom that<br />applies NFTs to connect real assets
          </p>
        </div>
        <div className="main__down-arrow-wrapper">
          <img className="main__down-arrow" src={DownArrow} alt="" onClick={() => Scroll("works")} />
        </div>
      </section>
      <section className="works" id="works" ref={WorksRef}>
        <div className="works__component">
          <h1 className="works__title title-text--bold">
            How does it work?
          </h1>
          <p className="works__sub-title title-text">
            Elysia creates contracts that convert real world assets into Non-Fungible Tokens (NFTs),<br />
            a unique digital identifier used to represent the underlying asset.<br/>
            ELYFI will utilize NFTs to connect reliable real world assets with cryptocurrency liquidity.
        </p>
          <div className="works__container">
            <img className="works__image" src={Service00} alt="" />
            <div className="works__text-wrapper scroll-animation">
              <h1 className="works__header-text">
                Blockchain
            </h1>
              <p className="works__text">
                We can experience the cryptocurrency financial system through blockchain.<br />All our contracts work logically, and all our records are stored in the blockchain. "ELYFI" is a new DEFI protocol and a project that everyone can participate in.
            </p>
            </div>
          </div>
          <div className="works__container">
            <div className="works__text-wrapper scroll-animation" data-sa-delay="200">
              <h1 className="works__header-text">
                Real Asset
              </h1>
              <p className="works__text">
                "ELYFI" links real-world assets with cryptocurrency financial systems.<br />Real assets are clearly valuable and secure, but they have a number of limitations.<br />Blockchain brings real assets to cryptocurrency financial systems to help make transactions more convenient.
              </p>
            </div>
            <img className="works__image scroll-animation scroll-animation--left" src={Service01} alt="" />
          </div>
          <div className="works__container">
            <img className="works__image  scroll-animation scroll-animation--right" src={Service02} alt="" />
            <div className="works__text-wrapper scroll-animation" data-sa-delay="200">
              <h1 className="works__header-text">
                NFT
              </h1>
              <p className="works__text">
                ELYFI creates a variety of contracts to connect reality with virtual currency financial systems.<br />These contracts are unique and must be trusted.<br />In this protocol, many contracts are created and managed as NFT(Non Fungible Token).
              </p>
            </div>
          </div>
        </div>
      </section>
      <section className="forum" id="forum" ref={ForumRef} style={{ height: '100%' }}>
        <Forum />
      </section>
    </div>
  );
}

export default Main;
