import { FunctionComponent, useState } from 'react';
import "../../css/style.scss";

interface Props {
  TokenName: string;
  Image: string;
  MarketSize?: number;
  TotalBorrowed?: number;
  DepositApy?: number;
  BorrowApr?: number
}

const TableListItem: FunctionComponent<Props> = (props: Props) => {
  const NumberFormat = (inputNumber: number) => {
    const si = [
      { value: 1, symbol: "" },
      { value: 1E3, symbol: " K" },
      { value: 1E6, symbol: " M" },
      { value: 1E9, symbol: " B" },
      { value: 1E12, symbol: " T" },
      { value: 1E15, symbol: " P" },
      { value: 1E18, symbol: " E" }
    ];
    const rx = /\.0+$|(\.[0-9]*[1-9])0+$/;
    var i;
    for (i = si.length - 1; i > 0; i--) {
      if (inputNumber >= si[i].value) {
        break;
      }
    }
    return (inputNumber / si[i].value).toFixed(2).replace(rx, "$1") + si[i].symbol;
  }
  return (
    <>
      <tr>
        <th>
          <div style={{ justifyContent: 'left', gap: 10 }}>
            <img src={props.Image} />
            <p>{props.TokenName}</p>
          </div>
        </th>
        <th>
          <p>
            {!props.MarketSize ? "-" : "$ " + NumberFormat(props.MarketSize) + "M"}
          </p>
        </th>
        <th>
          <p>
            {!props.TotalBorrowed ? "-" : "$ " + props.TotalBorrowed + "M"}
          </p>
        </th>
        <th>
          <p>
            {!props.DepositApy ? "-" : props.DepositApy.toFixed(2) + " %"}
          </p>
        </th>
        <th>
          <p>
            {!props.BorrowApr ? "-" : props.BorrowApr.toFixed(2) + " %"}
          </p>
        </th>
        <th>
          <div className="tokens__table-body-button">
            <p>Deposit</p>
          </div>
        </th>
        <th>
          <div className="tokens__table-body-button">
            <p>Borrow</p>
          </div>
        </th>
      </tr>
    </>
  );
}


export default TableListItem;
