import React, { useEffect } from 'react';
import {
  Switch,
  Route,
  BrowserRouter as Router,
} from "react-router-dom";
import "./i18n";

import TokenProvider from './providers/TokenProvider';

import Main from './modules/pc/main/Main';
import Service from './modules/pc/service/Service';

import MainM from './modules/mobile/main/Main';
import ServiceM from './modules/mobile/service/Service';

import { useMediaQuery } from 'react-responsive';
import { useTranslation } from 'react-i18next';

const App: React.FC = () => {
  const { i18n } = useTranslation();

  const isPc = useMediaQuery({
    query: "(min-width: 768px)"
  })

  useEffect(() => {
    let lang = navigator.language;
    lang = lang.toLowerCase().substring(0, 2);

    if (lang.includes('ko')) {
      i18n.changeLanguage('ko')
    } else if (lang.includes('zh')) {
      i18n.changeLanguage('zhHans')
    } else { 
      i18n.changeLanguage('en')
    }
  }, [])


  const PcRouter = () => {
    return (
      <Router>
        <Switch>
          <Route path="/service">
            <Service />
          </Route>
          <Route path="/">
            <Main />
          </Route>
        </Switch>
      </Router>
    );
  }
  const MobileRouter = () => {
    return (
      <Router>
        <Switch>
          <Route path="/service">
            <ServiceM />
          </Route>
          <Route path="/">
            <MainM />
          </Route>
        </Switch>
      </Router>
    );
  }


  return (
    <TokenProvider>
      {
      isPc ?
        <PcRouter />
        :
        <MobileRouter />
      }
    </TokenProvider>
  );
}

export default App;
